./reset.scss:
* {
    margin: 0;
    padding: 0;
    font-family: "微软雅黑";
}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-size: 100%;
    font-weight: 400;
}

details,
figcaption,
figure,
menu {
    display: block;
}

ol,
li {
    list-style: none;
}

input,
select,
textarea {
    outline: none;
}

img {
    display: block;
    border: 0 none;
}

i,
em {
    font-style: normal;
}

b {
    font-weight: 400;
}

table {
    border-collapse: collapse;
    border-spacing: 0;
}

q {
    quotes: none
}

a {
    cursor: pointer;
    text-decoration: none;
    font-family: "微软雅黑";
}


/* 清除浮动 */

.clearfix:after {
    content: '\0020';
    display: block;
    height: 0;
    clear: both
}

.clearfix {
    *zoom: 1
}


/* 省略号样式 */

.ellipsis {
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
}


/* 禁止选择  */

.unselected {
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -o-user-select: none;
    user-select: none;
}

